import React, {Component} from 'react'
import EmployeesList from './EmployeesList'
import Button from './Button'
import {Link} from 'react-router-dom';


class EmployeeSearch extends Component {
    constructor(props){
        super(props);
        this.state = { items: [], text: '', perPage: 5, currentPage: 0};
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    componentDidMount(){
        this.fetchFromApi();
      }

      fetchFromApi(){
        var url = 'http://localhost:8081/test';

        if (this.state.text)
          url += '?user=' + this.state.text;

        console.log('Fetching from: ' + url);

        fetch(url)
          .then(res => res.json())
          .then(json => {
              this.setState(state => ({
                items: json,
                text: ''
            }))
          });

      } 


    render(){
        const indexOfLastEmployee = this.state.currentPage * this.state.perPage;
        const indexOfFirstEmployee = indexOfLastEmployee - this.state.perPage;;
        const currentEmployee= this.state.items.slice(indexOfFirstEmployee, indexOfLastEmployee);

        return (
        <div>
            <h3>Search by first name:</h3>
            <form>
            <input
                id="new-todo"
                onChange={this.handleChange}
                value={this.state.text}
            />
            <Button text = "Search" onClick = {this.handleSubmit} />
            <Link to = "/add"> < Button text = "Add" /> </Link>
            </form>
            <EmployeesList items={currentEmployee} /> 
        </div>
        );

    }
    handleChange(e) {
        this.setState({ text: e.target.value });
      }



      handleSubmit(e) {
        e.preventDefault();
        if (this.state.text.length === 0) {
          return;
        }
        this.fetchFromApi();
      }




}

export default EmployeeSearch
