import React from 'react';
import Button from './Button';
import {Link} from 'react-router-dom';
import axios from 'axios';
import {fetchFromApi} from './Utils';
//import EmployeeSearch from './EmployeeSearch';

class EmployeesList extends React.Component {
  constructor(props){
    super(props);
    this.state = { items: [], text: ''};
    this.onDelete = this.onDelete.bind(this);
    this.fetchFromApi = fetchFromApi.bind(this);
  }
  render() {
    if(this.props.items.length > 0){
      return (
        <ul>
          {this.props.items.map(item => (
            <li className="employee-list" key={item.empNo}>ID: {item.empNo} - First Name: {item.firstName} - Last Name: {item.lastName} 
           <Link to ={`/edit/${item.empNo}`} ><Button text="Edit" /></Link> 
           <Button onClick= {() => this.onDelete(item.empNo)} text="Delete" />
            </li>
          ))}
        </ul>
      );
    }
    else{
      return (
        <h4>No Result</h4>
      );
    }

  }
  onDelete(id){
    console.log(" Delete function start");
    alert(`Employeed #${id} deleted successfully`);
    console.log(alert);
    axios.delete(`http://localhost:8081/test/delete/${id}`)
    console.log(`${id} deleted`)

    this.fetchFromApi();
    console.log(this.fetchFromApi);

  }






}

export default EmployeesList
