import React, { Component } from "react"
import axios from 'axios'
class EmployeeEdit extends Component {
    constructor(props) {
        super(props);
        this.state = { first_name: '' , last_name: ''};
        this.firstName = React.createRef();
        this.lastName = React.createRef();
      }
      componentDidMount(){
          this.getEmployeeDetails();
      }
      getEmployeeDetails(){
        let employeeID = this.props.match.params.id;
        axios.get(`http://localhost:8081/test?url=${employeeID}`)
        .then(res => {
            this.setState({
                first_name: res.data.firstName,
                last_name:  res.data.lastName
            }, () => {
                console.log(this.state);
            });
        })

      } 




    EditEmployee(newEmployee){
        axios.request({
            method: 'post',
            url: 'http://localhost:8081/test/create',
            data: newEmployee
        }).then(res =>{
            this.props.history.push('/');
        }).catch(err => console.log(err));
        //console.log(newEmployee);
    } 

    onSubmit(e){
        const newEmployee = {
            firstName: this.firstName.current.value,
            lastName: this.lastName.current.value
        }
        this.EditEmployee(newEmployee);
        //console.log(this.firstName.current.value)
        e.preventDefault();
    }

    render(){
        return (
            <div>
                <h1>Edit Employee</h1>
                <form id = "add-form" onSubmit = {this.onSubmit.bind(this)} >
                    <div>
                    <input type = "text" name = "First Name" ref = {this.firstName}  placeholder="Enter First Name" />
                    </div>
                    <br />
                    <div>
                    <input type = "text" name = "Last Name" ref = {this.lastName} placeholder = "Enter Last Name" />
                    </div>
                    <br />
                    <input type = "submit" value = "submit"/>


                </form>
            </div>
        )
    }


}

export default EmployeeEdit
