import React from 'react';
import Button from './Button'
import {Link} from 'react-router-dom'
import axios from 'axios';
class EmployeesList extends React.Component {
  render() {
    if(this.props.items.length > 0){
      return (
        <ul>
          {this.props.items.map(item => (
            <li key={item.empNo}>ID: {item.empNo} - First Name: {item.firstName} - Last Name: {item.lastName} 
           <Link to ={`/edit/${item.empNo}`} ><Button text="Edit" /></Link> 
           <Link to = {`delete/${item.empNo}`}><Button onClick={this.onDelete.bind(this)} text="Delete" /></Link>
            </li>
          ))}
        </ul>
      );
    }
    else{
      return (
        <h4>No Result</h4>
      );
    }

  }
  onDelete(){
    let meetupId = this.props.match.params.id;
    console.log(meetupId);
  }





}

export default EmployeesList
