import React from 'react';
import Button from './Button'
import {Link} from 'react-router-dom'
import axios from 'axios';
//import EmployeeSearch from './EmployeeSearch';
class EmployeesList extends React.Component {
  constructor(props){
    super(props);
    this.onDelete = this.onDelete.bind(this);
  }
  render() {
    if(this.props.items.length > 0){
      return (
        <ul>
          {this.props.items.map(item => (
            <li key={item.empNo}>ID: {item.empNo} - First Name: {item.firstName} - Last Name: {item.lastName} 
           <Link to ={`/edit/${item.empNo}`} ><Button text="Edit" /></Link> 
           <Button onClick= {() => this.onDelete(item.empNo)} text="Delete" />
            </li>
          ))}
        </ul>
      );
    }
    else{
      return (
        <h4>No Result</h4>
      );
    }

  }
  onDelete(id){
    axios.delete(`http://localhost:8081/test/delete/${id}`)
    .then(res =>{
      this.props.history.push('/')
    }).catch(err => console.log(err));
    alert(`Employee #${id} deleted sucessfully`);
  }





}

export default EmployeesList
