import React, { Component } from "react"
import Button from './Button'
import axios from 'axios'
class EmployeeAdd extends Component {
    constructor(props) {
        super(props);
        this.firstName = React.createRef();
        this.lastName = React.createRef();
      }
    onSubmit(e){
        console.log(this.firstName.current.focus())
        e.preventDefault();
    }
    render(){
        return (
            <div>
                <h1>Add Employee</h1>
                <form id = "add-form" onSubmit = {this.onSubmit.bind(this)} >
                    <div>
                    <input type = "text" name = "First Name" ref = {this.firstName}  placeholder="Enter First Name" />
                    </div>
                    <br />
                    <div>
                    <input type = "text" name = "Last Name" ref = {this.lastName} placeholder = "Enter Last Name" />
                    </div>
                    <br />
                    <input type = "submit" value = "submit"/>


                </form>
            </div>
        )
    }


}

export default EmployeeAdd
