export function fetchFromApi(){
    var url = 'http://localhost:8081/test';

    if (this.state.text)
      url += '?user=' + this.props.text;

    console.log('Fetching from: ' + url);

    fetch(url)
      .then(res => res.json())
      .then(json => {
          this.setState(state => ({
            items: json,
            text: ''
        }))
      });

  }
