import React, { Component } from 'react';
import EmployeesList from './components/EmployeesList.js'

class App extends Component{
  constructor(props) {
    super(props);
    this.state = { items: [], text: '' };
  }

  componentDidMount(){
    this.fetchFromApi();
  }

  fetchFromApi(){
    var url = 'http://localhost:8081';

    if (this.state.text)
      url += '/user=' + this.state.text;

    console.log('Fetching from: ' + url);

    fetch(url)
      .then(res => res.json())
      .then(json => {
          this.setState(state => ({
            items: json,
            text: ''
        }))
      });
  }

  render() {
    return (
      <div>
        <h3>Search by first name:</h3>
        <form onSubmit={this.handleSubmit.bind(this)}>
          <input
            id="new-todo"
            onChange={this.handleChange.bind(this)}
            value={this.state.text}
          />
          <button>
            Search
          </button>
        </form>
        <EmployeesList items={this.state.items} />
      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }



  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    this.fetchFromApi();
  }
}

export default App;
