import React, {Component } from 'react';
import EmployeesList from './components/EmployeesList'
import Button from './components/Button'
import { Link, Switch, Route } from 'react-router-dom';
import EmployeeEdit from './components/EmployeeEdit'


class App extends Component{
  constructor(props) {
    super(props);
    this.state = { items: [], text: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }


  componentDidMount(){
    this.fetchFromApi();
  }

  fetchFromApi(){
    var url = 'http://localhost:8081/test';

    if (this.state.text)
      url += '?user=' + this.state.text;

    console.log('Fetching from: ' + url);

    fetch(url)
      .then(res => res.json())
      .then(json => {
          this.setState(state => ({
            items: json,
            text: ''
        }))
      });



  } 


  render() {

    return (
      <div>
        <Switch>
          <Route path="/Add" exact component = {EmployeeEdit}/>
          <Route path="/" component = {App} />
        </Switch>
        <h3>Search by first name:</h3>
        <form>
          <input
            id="new-todo"
            onChange={this.handleChange}
            value={this.state.text}
          />
        <Button text = "Search" onClick = {this.handleSubmit} />
        <Link to = "/Add"> < Button text = "Add" /> </Link>
        </form>

        <EmployeesList items={this.state.items} />

      </div>
    );
  }

  handleChange(e) {
    this.setState({ text: e.target.value });
  }



  handleSubmit(e) {
    e.preventDefault();
    if (this.state.text.length === 0) {
      return;
    }
    this.fetchFromApi();
  }

}

export default App;
